/**
 * @function getOffsetFromWindow
 *
 * @param {HTMLElement} element The element to calculate it's offset.
 *
 * @return { { top: number; left: number } } Returns an object containing the offsets.
 */
export default function getOffsetFromWindow(
  // eslint-disable-next-line no-unused-vars
  element: HTMLElement
): { top: number; left: number };
