import { Ref } from "react";

/**
 * @function setRef
 *
 * @param {Ref<any>} ref The reference object/function.
 * @param {any} value The value needs to be set on the reference's current state.
 */
// eslint-disable-next-line no-unused-vars
export default function setRef(ref: Ref<any>, value: any): void;
