/**
 * Wraps the given value between range `[a, b]`.
 *
 * @function wrap
 *
 * @param {number} number The value to be wrapped.
 * @param {number} min The start of the range.
 * @param {number} max The end of the range.
 *
 * @return {number} The wrapped value.
 */
// eslint-disable-next-line no-unused-vars
export default function wrap(number: number, min: number, max: number): number;
